import { GET, POST, UPDATE } from '../constants/actions.js';
import * as api from '../api/Workspace.js';

export const getWorkspace = (formData, router) => async (dispatch) => {
	try {
		const { data } = await api.getWorkspaces();
		dispatch({ type: GET, data });
	} catch (error) {
		console.log(error);
	}
};

export const signup = (formData, router) => async (dispatch) => {
	try {
		const { data } = await api.signUp(formData);

		dispatch({ type: AUTH, data });

		router.push('/');
	} catch (error) {
		console.log(error);
	}
};
